# Splits list of texts into parts of consequetive text of given number of tokens
def split_text(texts, token_per_part=2000):
    """
    Splits list of texts into parts of given size
    """
    texts = [text.split() for text in texts]

    parts = [[[], 0]]
    for i, text in enumerate(texts):
        if i == 0:
            continue
        if len(parts[-1][0]) + len(text) > token_per_part:
            parts.append([[], 0])
        parts[-1][0].extend(text)
        parts[-1][1] += len(text)

    parts = [" ".join(part[0]) for part in parts if part]
    return parts

