def clashing_with_paths(
    selected_file: Path, existing_paths: Iterable[Path], must_delete=False
) -> int:
    clashes = 0
    for old_path in existing_paths:
        if old_path.exists() and old_path.absolute().resolve() == selected_file.absolute().resolve():
            if must_delete:
                old_path.unlink()
            clashes += 1
        old_path.parent.mkdir(parents=True, exist_ok=True)
    return clashes

