def encrypt(plaintext):
    # we manually capitalize plaintext because there is no regex available in Codenamers
    plaintext = plaintext.upper()
    lines = []
    line = []
    line_len = 5
    count = 0

    # we don't use i % 2 because Codenamers does not support the modulo operator
    if count % 2 == 0:
        append_flag = 0

    for c in plaintext:
        if len(line) == line_len and append_flag == 0:
            append_flag = 1
            lines.append(list(reversed(line)))
        elif len(line) == line_len and append_flag == 1:
            append_flag = 0
            lines.append(list(line))
            line_len += 5
        elif len(line) == line_len and append_flag is None:
            append_flag = 1
            lines.append(list(reversed(line)))
            line = []
        elif len(line) == line_len and append_flag == 0:
            append_flag = 1
            lines.append(list(reversed(line)))
            line = []
            line_len += 5
        else:
            line.append(c)
        count += 1

    if len(line) > 0 and append_flag == 0:
        append_flag = 1
        lines.append(list(reversed(line)))
    elif len(line) > 0 and append_flag == 1:
        append_flag = 0
        lines.append(list(line))
        line_len += 5

    l_enc = []
    for i, val in enumerate(lines):
        for c in val:
            l_enc.append(c)
            if (i + 1) % 2 == 0:
                l_enc.append("#")
    return "".join(l_enc)

def decrypt(ciphertext):
    ciphertext_as_list = list(ciphertext)
    lines = []

    while len(ciphertext_as_list) > 0:
        line = ciphertext_as_list[:5]
        lines.append(line)
        ciphertext_as_list = ciphertext_as_list[5:]

    kept_charlist = []
