def encrypt_text(plaintext, keyword):
    plaintext = plaintext.encode()
    sha_key = hashlib.sha256(keyword.encode()).hexdigest()

    ciphertext = bytes(bytearray(
        a ^ b for a, b in zip(plaintext, sha_key))
    ).hex()

    return ciphertext

def decrypt_text(ciphertext, keyword):
    ciphertext = bytes.fromhex(ciphertext)
    sha_key = hashlib.sha256(keyword.encode()).hexdigest()

    plaintext = bytes(bytearray(
        a ^ b for a, b in zip(ciphertext, sha_key)))

    return plaintext.decode()

