export function dataParserSolver(data: string) {
  const raw = data.split('\n');
  const puzzle = raw.map((row) => row.split('').map(char => char !== ' ' ? null : 0));
  const constraints1 = raw[raw.length - 1].split('');
  const constraints2 = raw[raw.length - 2].split('');
  const rowConstraints = constraints1.map((constraint) => Number(constraint));
  const colConstraints = constraints2.map((constraint) => Number(constraint));
  return { puzzle, rowConstraints, colConstraints };
