import java.util.Arrays;
import java.util.Collections;
import java.util.List;

class ExpressionEvaluator {

  public int evaluate(String expression) {
    int points = 0;
    List<String> list = Arrays.asList("zero", "one", "two", "three",
      "four", "five", "six", "seven", "eight", "nine", "ten", "eleven", "twelve", "thirteen", "fourteen", "fifteen",
      "sixteen", "seventeen", "eighteen", "nineteen", "start", "stop", "half", "begin", "continue", "cannot", "repeat",
      "is", "is not", "halfway", "halfway through", "unfinished", "round", "unround", "started", "started through");
    Collections.sort(list);

    for (int x = 0; x < expression.length(); x++) {
      if (expression.charAt(x) == '<' || expression.charAt(x) == '>' || expression.charAt(x) == '.') {
        for (int y = 0; y < 10; x++) {
          y++;
          if (expression.charAt(y) == '<' || expression.charAt(y) == '>')
            break;
          else if (y + 1 == list.size()) {
            points--;
            break;
          }
        }
        continue;
      }
      else if (expression.charAt(x) == ',' || expression.charAt(x) == ' ' || expression.charAt(x) == ')' ||
               expression.charAt(x) == '(') {
        continue;
      } else {
        int x_temp = y;
        boolean is_check = false;
        for (int j = 0; j < list.size(); j++) {
          if (expression.charAt(y) == list.get(j)) {
            y = x_temp;
            is_check = true;
            continue;
          }
        }

        if (!is_check && x_temp == list.size()) {
          points--;
          break;
        }
      }
    }

    return points;
  }
}
