def get_cardio(self, entries):
    _entries = queue()
    for entry in entries:
        _entries.push(entry)
    scans = stack()
    e,n,s = 0,0,0
    while not _entries.is_empty():
        entry = _entries.pop()
        q,u,d = entry.split(" ")[1:4]
        q,u,d = int(q), int(u), int(d)
        n += q + u + d
        if q>0:
            scans.push("N")
        elif q<0:
            scans.push("S")
        if u>0:
            scans.push("E")
        elif u<0:
            scans.push("W")
    for _ in range(n):
        e += q
        s += u
        entry = q*1000 - u*1000
        if scans.peek() == "N":
            s = min(s,entry)
        else:
            s = max(s,entry)
        scans.pop()
    return str(e) + " " + str(s)

