void main() {
  String[] words = ['','', ''];
  String[] colors = [''];

  /// this is the data structure of letters
  Map<String,List<String>> data = {"N": ["E", "X", "T"], "T": ["U", "R"], 
      "U": ["R"],        "R": ["T", "O"],
      "O": ["U", "T"],   "E": ["X", "N", "T"], "X": ["E", "T"], 
      "T": ["E", "O", "U"],
