      "M": ["E", "D", "I", "C", "A", "T", "I", "O", "N"],
      "D": ["A", "T", "I", "O"], "E": ["D", "I", 
      "N"], "I": ["D", "A"], "C": ["E", "A"], 
      "A": ["D", "T"], "T": ["E", "A"], 
      "O": ["T", "I", "N"], "S": ["E", "L", "F"]};

  /// Now basically for each word, we're trying to map the alphabet to a word.
  /// This logic can be written in any programming language.

  for(int i=0; i<colors.length; i++) {
    /// we're searching for a suitable word
    for(String letter in data[words[i]]){
      if(colors.contains(letter)){
        continue; /// this letter has been used already, so continue.
      }
      /// create the word from the matched letters
      words[i] += letter;
      /// remove the matched letter from the colors set
      colors.remove(letter);
      break;
    }
  }

  /// Final output in the required format
  print('[[${words[0]} ${words[1]} ${words[2]}]]');
}

