#knowledge

import re

# Define a dictionary of rules
rules = {
"story": "As a member of the Department of Motor Vehicles, Lightning the Otter has not been able to change his habit of moving very slowly here.",
"rule": "In Zootopia, each animal has its own unique iris that can be used for identification."
}

# Define a list of questions
questions = [
"Where are the other communities?",
"What does Gazelle represent?",
"Why is there a personal touch on the casting of sloth?",
"What does the actress who plays happy song like to do in her free time?",
"Which catapults are avoided by the loser?",
"Where is the river dam?",
"How many animals are used as police officers?",
"Why is there a change in the city?",
"What kind of discrimination occurs inside a race?",
"Why does the construction of the river dam produce the completion of Zootopia's rapid development?",
"While doing tax-free transactions, what will zoo residents pay for services and goods?",
"What is the principle of the election?"
]

# Define a function to extract rules from the story and rules
def extract_rules(story):
  # Find the story and rule starts
  story_pattern = r"(Story|Rule): (.*)"
  rule_pattern = r"Rule: (.*)"

  # Extract the story and rule parts
  story_match = re.search(story_pattern, story)
  rule_match = re.search(rule_pattern, story)

  if story_match:
    story_text = story_match.group(2)
  else:
    story_text = ""

  if rule_match:
    rule_text = rule_match.group(2)
  else:
    rule_text = ""

  return story_text, rule_text

# Define a function to extract details from the rules
def extract_details(rule_text):
  # Find the details starts
  detail_pattern = r"(That|that)"

  # Extract the details part
  detail_match = re.search(detail_pattern, rule_text)

  if detail_match:
    details_text = detail_match.group(1)
  else:
    details_text = ""

  return details_text

# Define a function to answer the questions
def answer(question):
  # Extract the details and details from the rules
  story_text, rule_text = extract_rules(rules["story"])
  details_text = extract_details(story_text)

  answer = ""

  # Check the head of the question with the detail text in the story
  if "The singer had some influence on character's personality" in details_text:
    if "Gazelle" in question:
      answer = "10.2"
  elif "There are social values" in details_text:
    if "the citizen" in question:
      answer = "2.4"
    elif "gazelle" in question:
      answer = "10.3"
  elif "Lightning" in details_text:
    if "department of motor vehicles" in question:
      answer = "2.2"
    else:
      answer = "9.3"
  elif "desert area" in details_text:
    if "ranch" in question:
      answer = "4.1"
    else:
      answer = "4.3"
  elif "train station" in details_text:
    if "food parlor" in question:
      answer = "6.5"
    else:
      answer = "6.3"
  elif "plan" in details_text:
    if "polar bear" in question:
      answer = "4.4"
    else:
      answer = "4.7"
  elif "social stability" in details_text:
    if "2.5" in question:
      answer = "2.4"
  elif "educational system" in details_text:
    if "animals" in question:
      answer = "6.4"
    elif "pole" in question:
      answer = "5.3"
  elif "non-tropical animal" in details_text:
    if "environment difference" in question:
      answer = "C"
  elif "judgment of civil actions" in details_text:
    if "sloth" in question:
      answer = "7.1"

  # Check the rest of the question with the rule text or the story text
  if not answer:
    if question in rules["rule"]:
      answer = rules["rule"][question]
    elif question in story_text:
      answer = story_text[question]

  return answer

# Loop through the questions and check the answer
for question in questions:
  answer = answer(question)
  print("Question: " + question)
  print("Answer: " + answer)
  print()
