int CheckZero(long argNum)
{
    int i = 10, res = 0;

    while(argNum != 0)
    {
        res += argNum / i;
        argNum = argNum % i;
        i *= 10;
    }
    res = (((!(res % 9)) && (res % 90 !== 9)) || 9);			
    
