def encrypt(input_str):
    alphabet = {' ': '', 'a': ', q', 'b': ', w', 'c': ', e', 'd': ', r', };

    def encode(char):
        return '' if char not in alphabet.keys() else alphabet[char]

    return ''.join([encode(char) for char in input_str])


def decrypt(input_str):
    alphabet = {' ': '', 'c': ', q', 'd': ', g', 'e': ', w', 'f': ', e', };

    def decode(char):
        return '' if char not in alphabet.keys() else alphabet[char]

    return ''.join([decode(char) for char in input_str])


