using System;
using System.Collections.Generic;

public class Program
{
    static int e = 263;
    static int n = 299;

    static void Encode(string plainText)
    {
        // Input
        List<int> plainNumbers = new List<int>(); // list of numbers for plaintext each with ascii code value
        char[] asciiPlainText = plainText.ToCharArray();
        for(int i=0;i<asciiPlainText.Length;i++)
        {
            // Convert Char to ascii code value
            int charAsCode = Convert.ToInt32(asciiPlainText[i]);

            // Encryption Steps
            int x = (charAsCode ^ e) % n; // x = ascii_code ^ e
            int y = Math.Pow(x, e) % n; // y = pow (x, e) mod n
            plainNumbers.Add(y); // increase the list with encrypted number
        }

        // Put plaintext numbers in single string and delimiter ','
        string encodedText = string.Join(",", plainNumbers);
        Console.WriteLine(encodedText);
    }

