def solve_game(matrix):
    n = len(matrix)
    arrows = [None] * n * n
    def backtrack(square):
        i, j = square // n, square % n
        if i * n + j == n * n:
            return True
        if 1 <= matrix[i][j] <= 9:
            for arrow in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
                valid = True
                x, y = i + arrow[0], j + arrow[1]
                count = 0
                while 0 <= x < n and 0 <= y < n and (arrows[x * n + y] is None or arrows[x * n + y] == arrow):
                    count += 1
                    x += arrow[0]
                    y += arrow[1]
                if (
                    (matrix[i][j] != count) or
                    any(0 > x or x >= n or 0 > y or y >= n or (arrows[x * n + y] is not None and arrows[x * n + y] != arrow) for x in range(i + arrow[0], i + (arrow[0] * (matrix[i][j] - 1)) + 1, arrow[0]) for y in range(j + arrow[1], j + (arrow[1] * (matrix[i][j] - 1)) + 1, arrow[1]))
                ):
                    valid = False
                    break
                if valid:
                    x, y = i + arrow[0], j + arrow[1]
                    while 0 <= x < n and 0 <= y < n:
                        arrows[x * n + y] = arrow
                        x += arrow[0]
                        y += arrow[1]
                    if backtrack(i * n + j + 1):
                        return True
                    x, y = i + arrow[0], j + arrow[1]
                    while 0 <= x < n and 0 <= y < n:
                        arrows[x * n + y] = None
                        x += arrow[0]
                        y += arrow[1]
        else:
            if backtrack(i * n + j + 1):
                return True
        return False
    backtrack(0)
    return [[(arrows[i * n + j][0] + 1) * 3 + (arrows[i * n + j][1] + 1) if arrows[i * n + j] is not None else "_" for j in range(n)] for i in range(n)]

