def permutations(n):
    if n <= 1:
        return [[n]]
    result = []
    for i in range(n):
        subpermutations = permutations(n-1)
        for j in range(n):
            for subpermutation in subpermutations:
                newpermutation = list(subpermutation)
                newpermutation.insert(j,n)
                result.append(newpermutation)
    return result

