def valid_domino_rules(matrix):
    N = len(matrix)
    # Create regions based on the unique chars in the matrix.
    regions = set(''.join(set(''.join(matrix[i]) for i in range(N))))

    matrix_in_nums = [['0' if cell==' ' else str(i+1) for i, cell in enumerate(row)]
                      for row in matrix]

    # Check each char
    dominoes = []
    for region in regions:
        for index, char in enumerate(matrix_in_nums):
            # Check each cell in the region
            for i in range(len(char)):
                for j in range(len(char[i])):
                    if char[i][j] == region:
                        # Check for left possibility
                        if j-1>=0 and matrix_in_nums[i][j-1] == region:
                            # Check if both indices form a valid domino index
                            if (i,j-1, i, j) not in dominoes:
                                dominoes.append((i, j-1, i, j))

                        # Check for up possibility
                        if i-1>=0 and matrix_in_nums[i-1][j] == region:
                            if (i-1,j, i, j) not in dominoes:
                                dominoes.append((i-1, j, i, j))

    return dominoes

