    for i, morse_char in enumerate(morse):
        if len(morse_char) == 2:
            plaintext += morse_code[morse_char.split("/-")[0]]
        else:
            plaintext += morse_code[morse_char.split("/-")[0]]
    return plaintext.upper()

# Reverse of the above function
def convert_morse_to_ciphertext(morse):
    ciphertext = ""
    for i, morse_char in enumerate(morse):
        if len(morse_char) == 2:
            ciphertext += list(morse_code[morse_char.split("/-")[0]])
        else:
            ciphertext += list(morse_code[morse_char.split("/-")[0]])
    return ciphertext
