def encrypt(plain_text, secret_key):
    plain_bytes = bytes(plain_text, "ascii")
    encrypted_bytes = []
    for i in plain_bytes:
        encrypted_byte = i ^ ord(secret_key[i % len(secret_key)])
        encrypted_bytes.append(encrypted_byte)
    return b"".join(encrypted_bytes)
