def decrypt(ciphertext, secret_key):
    ciphertext_bytes = bytes(ciphertext, "ascii")
    decrypted_bytes = []
    for j in ciphertext_bytes:
        decrypted_byte = j ^ ord(secret_key[j % len(secret_key)])
        decrypted_bytes.append(decrypted_byte)
    return b"".join(decrypted_bytes)
