def draw_arrows(question, n):
    # Split the question by commas, then by spaces to get the matrix
    matrix = [[int(x) if x.isdigit() else x for x in row.split()] for row in question.split(',')]

    def is_valid(r, c, direction):
        if direction == '←':
            for i in range(c-1, -1, -1):
                if matrix[r][i] == 'X':
                    matrix[r][i] = '→'
                else:
                    return False
        elif direction == '→':
            for i in range(c+1, n):
                if matrix[r][i] == 'X':
                    matrix[r][i] = '←'
                else:
                    return False
        elif direction == '↑':
            for i in range(r-1, -1, -1):
                if matrix[i][c] == 'X':
                    matrix[i][c] = '↓'
                else:
                    return False
        elif direction == '↓':
            for i in range(r+1, n):
                if matrix[i][c] == 'X':
                    matrix[i][c] = '↑'
                else:
                    return False
        return True

    for r in range(n):
        for c in range(n):
