class AI_Assistant:
    def __init__(self):
        self.operations = {}
        self.vocabulary = []
        self.questions = []
        self.answers = []

    def add_operation(self, operation):
        self.operations[operation["name"]] = operation

    def add_vocabulary(self, vocabulary):
        self.vocabulary += vocabulary

    def add_question(self, question):
        self.questions.append(question)

    def add_answer(self, name, answer):
        if name not in self.operations:
            raise ValueError(f"Invalid operation name: {name}")
        self.answers.append(answer)

    def get_operations(self):
        return self.operations

    def get_vocabulary(self):
        return self.vocabulary

    def get_questions(self):
        return self.questions

    def get_answers(self):
        return self.answers

    def get_data(self):
        return {"operations": self.get_operations(), "vocabulary": self.get_vocabulary(), "questions": self.get_questions(), "answers": self.get_answers()}

    def parse_text(self, text):
        pattern = r"<a,b,c,d>=2ab+c-d.(.*),"
        matches = re.findall(pattern, text)
        if matches:
            for match in matches:
                terms = match.split("=")
                if len(terms) != 2:
                    error_msg = f"Unable to find <a,b,c,d>=2ab+c-d.(.*), in text."
                    self.questions.append({"error": error_msg})
                    return
                variables = terms[0].split(",")
                new_formula = re.sub(r"<(\d+),(\d+),(\d+),(\d+)>", f"{{a,b,c,d}}", terms[1])
                question = {
                    "template": "Compute <%s>." % variables[0],
                    "input": text,
                    "error": None,
                    "output": {"answer_text": None, "format": "text"},
                    "grading_feedback": None,
                    "user_feedback": {"quality score": None}
                }
                self.questions.append(question)
                self.answers.append({
                    "name": new_formula,
                    "solution": "",
                    "solution_format": "text",
                    "format": "text"
                })
                error_msg = f"Unable to find {len(variables)} <a,b,c,d>=2ab+c-d.(.*), in text."
                self.questions.append({"error": error_msg})

    def get_service_answer(self):
        data = self.get_data()
        url = "http://127.0.0.1:8000/ai_assistant/"
        response = requests.post(url, json=data)
        answer = response.json()
        return answer

