polybius_square = [
    'RTFXFS',
    'WCMVH',
    'ZJAPB',
    'LQYGK',
    'NESUI'
]

ciphertext = "4541"

def decrypt(ciphertext):
    cipher_parts = list(map(int, ciphertext.split()))
    decrypted_chars = []

    for cr, cc in cipher_parts:
        if cr == 6 and cc == 6:
            decrypted_chars.append('O')
        else:
            try:
                row = cr - 1
                col = cc - 1
                decrypted_chars.append(polybius_square[row][col])
            except IndexError:
                decrypted_chars.append('ERROR')

    return ''.join(decrypted_chars)

decrypted_answer = decrypt(ciphertext)
