class Solution {
public:
    vector<vector<int>> countNums(vector<vector<char>>& grid) {
        vector<vector<int>> gridCount(grid);
        for (int i = 0; i < grid.size(); ++i) {
            for (int j = 0; j < grid.front().size(); ++j) {
                if (grid[i][j] == '.' || grid[i][j] == 'X') {
                    for (int a = -2; a <= 2; ++a) {
                        for (int b = -2; b <= 2; ++b) {
                            if (a == 0 && b == 0)
                                continue;
                            if (i + a >= 0 && i + a < grid.size() && j + b >= 0 && j + b < grid.front().size() && (grid[i + a][j + b] == 'X' || grid[i + a][j + b] == '#')) {
                                ++gridCount[i][j];
                            }
                        }
                    }
                }
            }
        }
        return gridCount;
    }
};
