private int[] solvePuzzle(int[][] board, int[][] relations, int[][] pre) {
    final Stack<Step> steps = new Stack<>();
    int[] result = new int[board.length];
    for (int i = 0; i < board.length; i++) {
        final int[] prev_board;
        if (i == 0) {
            prev_board = board[i];
        } else {
            prev_board = result;
        }
        for (int c = 0; c < prev_board.length; c++) {
            Step step = new Step(i, c);
            steps.push(step);
        }
    }
    while (!steps.isEmpty()) {
        Step step = steps.pop();
        final int row_number = step.row_number;
        final int col_number = step.col_number;
        if (step.late) {
            result[row_number] = relations[row_number][col_number];
            if (--step.counter == 0) {
                step.append(result[row_number], relations, row_number, col_number, pre);
                step.late = true;
            }
        } else {
            step.counter = 0;
            final int[] tmp_board = board[row_number];
            for (int c = 0; c < tmp_board.length; c++) {
                int[] tmp_board_too = new int[tmp_board.length];
                Arrays.fill(tmp_board_too, -1);
                tmp_board_too[c] = tmp_board[c];
                appendStep(step, row_number, c, tmp_board_too*, relations, *pre);
            }
        }
        step.onset = true;
        if (step.late || step.onset) {
            steps.add(step);
        }
    }
    return result;
}

