    public int optimalSelects(int[] A, int n) {
        Queue<int[]> queue = new  LinkedList<>();
        Queue<int[]> sizeQueue = new  LinkedList<>();
        queue.add(A); 
        while(!queue.isEmpty()){
            int[] queueSize =sizeQueue.poll();
            if(queueSize[0]>=A.length/5){
                return queueSize[1];

            }else{
                for(int i = 1; i<=A.length-queueSize[0];i++){
                    int[] item = new int[2];
                    item[0] =  queueSize[0]+i;                   
                    item[1] = A[item[0]];
                    queue.offer(item);
                }
            }
        }
        return -1;
    }
}
