import java.util.*;

public class Encryption {
    public static String encryption(String ciphertext) {
        final String ALPHABET = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
        String decryptedCiphertext = "";

        // rules for encryption (odd to even)
        String chars = "";
        int lineCount = 1;
        ciphertext = ciphertext.toUpperCase().replaceAll("[^A-Z]", "");

        for (int i = 0; i < ciphertext.length(); i++) {
            if (ciphertext.charAt(i) == '_') {
                // move on to the next column
                if (lineCount % 2 == 1) {
                    chars += ciphertext.substring(i + 1, i + 2) + "#";
                    decryptedCiphertext = decryptedCiphertext.concat(chars) + " ";
                } else {
                    // go left to right
                    lineCount++;
                    if (lineCount > 5) {
                        lineCount = 1;
                    }
                    lineCount++;
                    i++;
                    chars = ciphertext.substring(i + 1, i + 2);
                    decryptedCiphertext = decryptedCiphertext.concat(chars) + " ";
                }

            } else {
                // go off to the left
                decryptedCiphertext = decryptedCiphertext.concat(ciphertext.substring(i) + " ");
            }
        }

        // read per line, replace '#' occurrency
        return decryptedCiphertext.replaceAll(" #", "");
    }

