def find_word(grid, word, row, col, direction):
    if direction == 'H':  # Horizontal search
        start_col = col
        for i in range(len(word)):
            if grid[row][start_col + i] != word[i]:
                return False
    elif direction == 'V':  # Vertical search
        start_row = row
        for i in range(len(word)):
            if grid[start_row + i][col] != word[i]:
                return False
    elif direction == 'D':  # Diagonal search
        start_row = row
        start_col = col
        for i in range(len(word)):
            if grid[start_row + i][start_col + i] != word[i]:
                return False
    elif direction == 'U':  # Anti-diagonal search
        start_row = row
        start_col = col
        for i in range(len(word)):
            if grid[start_row + i][start_col - i] != word[i]:
                return False
    elif direction == 'R':  # Right search
        start_col = col
        for i in range(len(word)):
            if grid[row][start_col + len(word) - i - 1] != word[i]:
                return False
    elif direction == 'L':  # Left search
        start_col = col
        for i in range(len(word)):
            if grid[row][start_col - i - 1] != word[i]:
                return False
    elif direction == 'LR':  # Diagonal right search
        start_row = row
        start_col = col
        for i in range(len(word)):
            if grid[start_row + i][start_col + len(word) - i - 1] != word[i]:
                return False
    elif direction == 'UL':  # Anti-diagonal left search
        start_row = row
        start_col = col
        for i in range(len(word)):
            if grid[start_row + i][start_col - i - 1] != word[i]:
                return False
    elif direction == 'UD':  # Diagonal up search
        start_row = row
        start_col = col
        for i in range(len(word)):
            if grid[start_row - i - 1][start_col + i] != word[i]:
                return False
    elif direction == 'LU':  # Anti-diagonal up search
        start_row = row
        start_col = col
        for i in range(len(word)):
            if grid[start_row - i - 1][start_col - i - 1] != word[i]:
                return False

    # Word was found, return its coordinates
    if direction == 'H':
        return (row, start_col, row, start_col + len(word) - 1)
    elif direction == 'V':
        return (start_row, col, start_row + len(word) - 1, col)
    elif direction == 'D' or direction == 'UD':
        return (start_row, start_col, start_row + len(word) - 1, start_col + len(word) - 1)
    elif direction == 'U' or direction == 'LU':
        return (start_row, start_col, start_row + len(word) - 1, start_col - len(word) + 1)
    elif direction == 'R':
        return (row, start_col + len(word) - len(word), row, start_col + len(word) - 1)
    elif direction == 'L':
        return (row, start_col, row, start_col - len(word) + 1)
    elif direction == 'LR':
        return (start_row, start_col + len(word) - len(word), start_row + len(word) - 1, start_col + len(word) - 1)
    elif direction == 'UL':
        return (start_row, start_col - len(word) + 1, start_row + len(word) - 1, start_col - 1)
    elif direction == 'D':
        return (start_row, start_col, start_row + len(word) - 1, start_col + len(word) - 1)
