def find_mines(grid):
  n = len(grid)

  # Iterate through each cell in the grid
  for i in range(n):
    for j in range(n):
      if grid[i][j] != '1' and grid[i][j] != '2' and grid[i][j] != 'X':
        return "Grid cell value must be either 1 and 2."

  # Iterate through each cell to calculate the number of mines around them
  for i in range(n):
    for j in range(n):
      # Ignore the cell when calculating the number of mines if it's an X
      if grid[i][j] == 'X':
        continue

      # Count the number of mines for the current cell
      mine_count = 0

      # Check all adjacent cells
      for x in range(-1, 2):
        for y in range(-1, 2):
          # Check if the adjacent cell is within the grid and is a mine
          if grid.get((i + x), None) == None or grid.get((i + x)) == None or grid.get((i+x, j+y), None) == None or grid.get((i+x, j+y))[j+y] == None or grid.get((i+x, j+y)) == None or (x == 0 and y == 0):
